import React, { useContext, useState, useEffect } from "react";
import MovieList from "../../api/MovieList";
import { MovieContext } from "../../context/MovieContext";
import { useParams } from "react-router-dom";

export default function VideoPlayer() {
  const { id } = useParams();
  // const [movieid, setMovieid] = useState(id);

  const { movies, setMovies } = useContext(MovieContext);
  const [actors, setActors] = useState("");
  const [director, setDirector] = useState("");
  const [rating, setRating] = useState(0);
  const [hover,setHover]=useState(0)
  const fetchData = async () => {
    try {
      const video = await MovieList.get(`/get-movie-list/${id}`);
      setMovies(video.data);
      const adetail = await MovieList.get(`/actor-details/${id}`);
      setActors(adetail.data.info);
      const ddetail = await MovieList.get(`/director-details/${id}`);
      setDirector(ddetail.data.info);
      console.log(adetail.data.info.fname);
      //console.log(video.data);
    } catch (error) {
      console.log(error.message);
      //redirect to homepage
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className=" grid grid-rows-1 gap-4 w-full min-h-screen justify-center items-center pt-10">
      <div>
        <iframe
          width="1000"
          height="500"
          src={movies?.data?.movie_title}
          title="YouTube video player"
          frameborder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowfullscreen
        ></iframe>
      </div>
      <div className="mt-10">
        <h1 className="font-bold text-4xl dark:text-black">Movie Cast</h1>
      </div>

      <div className="grid grid-cols-2 gap-4">
        {actors &&
          actors.map((actor) => {
            return (
              <div className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-300 dark:border-gray-700 dark:hover:bg-gray-700">
                <div key={actor?.movieid} class="flex items-center space-x-4 ">
                  <img
                    class="w-36 h-36 rounded-full"
                    src={actor?.aimg_link}
                    alt=""
                  />

                  <div className="space-y-1 font-medium dark:text-white">
                    <div className="text-black">
                      {actor?.fname.concat(" ".concat(actor.lname))}
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
      </div>

      <div className="mt-10">
        <h1>Director</h1>
      </div>
      <div>
        {director &&
          director.map((director) => {
            return (
              <div className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-300 dark:border-gray-700 dark:hover:bg-gray-700">
                <div class="flex items-center space-x-4 ">
                  <img
                    class="w-36 h-36 rounded-full"
                    src={director?.dimg_link}
                    alt=""
                  />
                  <div class="space-y-1 font-medium dark:text-white ">
                    <div className="text-black hover:text-white ">
                      {director?.fname.concat(" ".concat(director.lname))}
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
      </div>

      <div className="star-rating">
      {[...Array(5)].map((star, index) => {
        index += 1;
        return (
          <button
            type="button"
            key={index}
            className={index <= (hover || rating) ? "on" : "off"}
            onClick={() => setRating(index)}
            onMouseEnter={() => setHover(index)}
            onMouseLeave={() => setHover(rating)}
          >
            <span className="star"><svg
            class="w-5 h-5 text-yellow-400"
            fill="currentColor"
            viewBox="0 0 20 20"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path>
          </svg></span>
          </button>
        );
      })}
    </div>
    <p></p>
      {/* <div class="flex items-center">
          <svg
            class="w-5 h-5 text-yellow-400"
            fill="currentColor"
            viewBox="0 0 20 20"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path>
          </svg>

          <svg
            class="w-5 h-5 text-gray-300 dark:text-gray-500"
            fill="currentColor"
            viewBox="0 0 20 20"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path>
          </svg>
        </div> */}
    </div>
  );
}
