import React, { useContext, useState, useEffect } from "react";
import MovieList from "../../api/MovieList";
import { MovieContext } from "../../context/MovieContext";
import { useParams } from "react-router-dom";

export default function VideoPlayer() {
  const { id } = useParams();
  // const [movieid, setMovieid] = useState(id);

  const { movies, setMovies } = useContext(MovieContext);
  const [actors, setActors] = useState("");
  const [director, setDirector] = useState("");

  const fetchData = async () => {
    try {
      const video = await MovieList.get(`/get-movie-list/${id}`);
      setMovies(video.data);
      const adetail = await MovieList.get(`/actor-details/${id}`);
      setActors(adetail.data.info);
      const ddetail = await MovieList.get(`/director-details/${id}`);
      setDirector(ddetail);
      console.log(adetail.data.info.fname);
      //console.log(video.data);
    } catch (error) {
      console.log(error.message);
      //redirect to homepage
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className=" grid grid-rows-1 gap-4">
      <div>
        <iframe
          width="1000"
          height="500"
          src={movies?.data?.movie_title}
          title="YouTube video player"
          frameborder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowfullscreen
        ></iframe>
      </div>
      <div className="mt-10">
        <h1>Movie Cast</h1>
      </div>
      <div className="grid grid-cols-2 gap-4">
        {actors &&
          actors.map((actor) => {
            return (
              <div className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-300 dark:border-gray-700 dark:hover:bg-gray-700">
                <div key={actor?.movieid} class="flex items-center space-x-4 ">
                  <img
                    class="w-36 h-36 rounded-full"
                    src={actor?.aimg_link}
                    alt=""
                  />

                  <div className="space-y-1 font-medium dark:text-white">
                    <div className="text-black">
                      {actor?.fname.concat(" ".concat(actor.lname))}
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
      </div>

      <div className="mt-10">
        <h1>Director</h1>
      </div>
      <div>
        {director &&
          director.map((director) => {
            return (
              <div className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-300 dark:border-gray-700 dark:hover:bg-gray-700">
                <div class="flex items-center space-x-4 ">
                  <img
                    class="w-36 h-36 rounded-full"
                    src={director?.dimg_link}
                    alt=""
                  />
                  <div class="space-y-1 font-medium dark:text-white ">
                    <div className="text-black hover:text-white ">
                      {director?.fname.concat("".concat(director.lname))}
                    </div>
                    <div class="text-sm text-gray-500 dark:text-gray-400">
                      Joined in August 2014
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
      </div>
    </div>
  );
}
