import React, { useState } from "react";
import MovieList from "../../api/MovieList";

export default function AddMovie() {
  const [title, setTitle] = useState("");
  const [rdate, setRdate] = useState("");
  const [rating, setRating] = useState("");
  const [link, setLink] = useState("");
  const [desc, setDesc] = useState("");
 
  const [genreid, setGenreid] = useState("");
  const [dfname, setDfname] = useState("");
  const [dlname, setDlname] = useState("");
  const [dgender, setDgender] = useState("");
  const [noOfMovies, setNoOfMovies] = useState("");
  const [afname, setAfname] = useState("");
  const [alname, setAlname] = useState("");
  const [agender, setAgender] = useState("");
  const [role, setRole] = useState("");

  const handleChanges = async (e) => {
    e.preventDefault();
    try {
      const response = await MovieList.post("/add-movie-list", {
        title,
        date_of_release: rdate,
        movie_desc: desc,
        genreid: genreid,
        link,
        imdb_rating: rating,
      });
      console.log(response);
    } catch (error) {
      console.log(error.message);
    }
  };

  return (
    <>
      <div className="min-h-full py-12 px-4 sm:px-6 lg:px-8">
        <form action="#" method="POST" onSubmit={handleChanges}>
          <div className="shadow overflow-hidden sm:rounded-md">
            <div className="px-8 py-5 bg-white sm:p-6">
              <div className="grid grid-cols-6 gap-6">
                <div className="col-span-6 sm:col-span-6 lg:col-span-4">
                  <label
                    htmlFor="title"
