import React, { useState } from "react";
import MovieList from "../../api/MovieList";

export default function AddMovie() {
  const [title, setTitle] = useState("");
  const [rdate, setRdate] = useState("");
  const [rating, setRating] = useState("");
  const [link, setLink] = useState("");
  const [desc, setDesc] = useState("");
  const [genreid, setGenreid] = useState("");
  const [posterLink,setPosterLink]=useState("");


  const [dfname, setDfname] = useState("");
  const [dlname, setDlname] = useState("");
  const [dgender, setDgender] = useState("");
  const [noOfMovies, setNoOfMovies] = useState("");
  const [dimg,setDimg]=useState("");


  const [a1fname, setA1fname] = useState("");
  const [a1lname, setA1lname] = useState("");
  const [a1gender, setA1gender] = useState("");
  const [role1, setRole1] = useState("");
  const [a1img,setA1img]=useState("");

  const [a2fname, setA2fname] = useState("");
  const [a2lname, setA2lname] = useState("");
  const [a2gender, setA2gender] = useState("");
  const [role2, setRole2] = useState("");
  const [a2img,setA2img]=useState("");

  {/*Director table*/}



  const handleChanges = async (e) => {
    e.preventDefault();
    try {
      const response = await MovieList.post("/movies/add-movie-list", {
        title,
        date_of_release: rdate,
        movie_desc: desc,
        genreid: genreid,
        poster_link:posterLink,
        link,
        imdb_rating: rating,
      });
      const dres=await MovieList.post("/reg-director",{
        fname:dfname,
        lname:dlname,
        gender:dgender,
        no_of_movies:noOfMovies,
        dimg_link:dimg
      });
      const a1res=await MovieList.post("/reg-director",{
        fname:dfname,
        lname:dlname,
        gender:dgender,
        no_of_movies:noOfMovies,
        dimg_link:dimg
      });
      console.log(response);
    } catch (error) {
      console.log(error.message);
    }
  };

  return (
    <>
      <div className="min-h-full py-12 px-4 sm:px-6 lg:px-8">
        <form action="#" method="POST" onSubmit={handleChanges}>
          <div className="shadow overflow-hidden sm:rounded-md">
            <div className="px-8 py-5 bg-white sm:p-6">
              <div className="grid grid-cols-6 gap-6">
                <div className="col-span-6 sm:col-span-6 lg:col-span-4">
                  <label
                    htmlFor="title"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Movie Title
                  </label>
                  <input
                    type="text"
                    name="title"
                    id="title"
                    value={title}
                    onChange={(e) => {
                      setTitle(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
                <div className="col-span-6 sm:col-span-4 lg:col-span-2">
                  <label
                    htmlFor="rdate"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Date Of Release
                  </label>
                  <input
                    type="date"
                    name="rdate"
                    id="rdate"
                    value={rdate}
                    onChange={(e) => {
                      setRdate(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
                <div className="col-span-6">
                  <label
                    htmlFor="link"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Movie Link
                  </label>
                  <input
                    type="text"
                    name="link"
                    id="link"
                    value={link}
                    onChange={(e) => {
                      setLink(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
                <div className="col-span-6">
                  <label
                    htmlFor="link"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Poster Link
                  </label>
                  <input
                    type="text"
                    name="posterLink"
                    id="link"
                    value={link}
                    onChange={(e) => {
                      setLink(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
                <div className="col-span-6">
                  <label
                    htmlFor="desc"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Movie Description
                  </label>
                  <textarea
                    rows="4"
                    cols="50"
                    name="desc"
                    id="desc"
                    value={desc}
                    onChange={(e) => {
                      setDesc(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>

                <div className="col-span-6 sm:col-span-3">
                  <lable
                    htmlFor="rating"
                    className="block text-sm font-medium text-gray-700"
                  >
                    IMDB rating
                  </lable>
                  <input
                    type="number"
                    name="rating"
                    value={rating}
                    onChange={(e) => {
                      setRating(e.target.value);
                    }}
                    id="rating"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>

                

                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="postal-code"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Genre ID
                  </label>
                  <input
                    type="number"
                    name="genreid"
                    id="genreid"
                    value={genreid}
                    onChange={(e) => {
                      setGenreid(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
                <hr className="col-span-6 "></hr>
                <div className="col-span-6 sm:col-span-3 ">
                  <lable className="block text-sm font-medium text-gray-700">
                    Director First Name
                  </lable>
                  <input
                    type="text"
                    name="dfname"
                    id="dfname"
                    value={dfname}
                    onChange={(e) => {
                      setDfname(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>

                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="dlname"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Director's Last Name
                  </label>
                  <input
                    type="text"
                    name="dlname"
                    value={dlname}
                    onChange={(e) => {
                      setDlname(e.target.value);
                    }}
                    id="dlname"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>

                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="dgender"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Gender
                  </label>
                  <input
                    type="text"
                    name="dgender"
                    id="dgender"
                    value={dgender}
                    onChange={(e) => {
                      setDgender(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="noOfMovies"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Number of Movies Directed
                  </label>
                  <input
                    type="number"
                    name="noOfMovies"
                    id="noOfMovies"
                    value={noOfMovies}
                    onChange={(e) => {
                      setNoOfMovies(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>

                {/*1st actor*/}
                <hr className="col-span-6 "></hr>
                
                <div className="col-span-6 sm:col-span-3 ">
                
                  <lable className="block text-sm font-medium text-gray-700">
                    Actor1's First Name
                  </lable>
                  <input
                    type="text"
                    name="a1fname"
                    id="a1fname"
                    value={a1fname}
                    onChange={(e) => {
                      setA1fname(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>

                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="a1lname"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Actor1's Last Name
                  </label>
                  <input
                    type="text"
                    name="a1lname"
                    id="a1lname"
                    value={a1lname}
                    onChange={(e) => {
                      setA1lname(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>

                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="a1gender"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Actor 1's Gender
                  </label>
                  <input
                    type="text"
                    name="a1gender"
                    id="a1gender"
                    value={a1gender}
                    onChange={(e) => {
                      setA1gender(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="role1"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Actor 1's Cast Role
                  </label>
                  <input
                    type="text"
                    name="role1"
                    id="role1"
                    value={role1}
                    onChange={(e) => {
                      setRole1(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
                {/*2nd actor details*/}
                <hr className="col-span-6 "></hr>
                <div className="col-span-6 sm:col-span-3 ">
                  
                  <lable className="block text-sm font-medium text-gray-700">
                    Actor 2's First Name
                  </lable>
                  <input
                    type="text"
                    name="a2fname"
                    id="a2fname"
                    value={a2fname}
                    onChange={(e) => {
                      setA2fname(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>

                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="a2lname"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Actor 2's Last Name
                  </label>
                  <input
                    type="text"
                    name="a2lname"
                    id="a2lname"
                    value={a2lname}
                    onChange={(e) => {
                      setA2lname(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>

                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="a2gender"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Actor 2's Gender
                  </label>
                  <input
                    type="text"
                    name="a2gender"
                    id="a2gender"
                    value={a2gender}
                    onChange={(e) => {
                      setA2gender(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
                <div className="col-span-6 sm:col-span-3 ">
                  <label
                    htmlFor="role2"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Actor 2's Cast Role
                  </label>
                  <input
                    type="text"
                    name="role2"
                    id="role2"
                    value={role2}
                    onChange={(e) => {
                      setRole2(e.target.value);
                    }}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outlin"
                  />
                </div>
              </div>
              
            </div>
            <div className="px-4 py-3 bg-gray-50 text-right sm:px-6">
              <button
                type="submit"
                className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                Save
              </button>
            </div>
          </div>
        </form>
      </div>
    </>
  );
}
