import React, { useContext, useState, useEffect } from "react";
import MovieList from "../../api/MovieList";
import { MovieContext } from "../../context/MovieContext";
import { useNavigate, useParams } from "react-router-dom";

export default function VideoPlayer() {
  const { id } = useParams();
  const nav=useNavigate();

  // const [movieid, setMovieid] = useState(id);

  const { movies, setMovies } = useContext(MovieContext);
  const [actors, setActors] = useState("");
  const [director, setDirector] = useState("");
  const [comments, setComments] = useState("");
  const [firstname, setFirstname] = useState("");
  const [hover, setHover] = useState(0);
  const fetchData = async () => {
    try {
      const video = await MovieList.get(`/get-movie-list/${id}`);
      setMovies(video.data);
      const adetail = await MovieList.get(`/actor-details/${id}`);
      setActors(adetail.data.info);
      const ddetail = await MovieList.get(`/director-details/${id}`);
      setDirector(ddetail.data.info);
      console.log(adetail.data.info.fname);

      //console.log(video.data);
    } catch (error) {
      console.log(error.message);
      //redirect to homepage
    }
  };

  const addComment = async (e) => {
    e.preventDefault();
    try {
      const user = await MovieList.put(`/update-comments/${id}`, {
        fname: firstname,
        comments: comments,
      });
      windows
      

      console.log(user?.data);
    } catch (error) {
      console.log(error.message);
    }
  };

  const style = { on: { color: "#000" }, off: { color: "#111" } };

  useEffect(() => {
    fetchData();
    addComment();
  }, []);

  return (
    <div className=" grid grid-rows-1 gap-4 w-full min-h-screen justify-center items-center pt-10">
      <div>
        <iframe
          width="1000"
          height="500"
          src={movies?.data?.movie_title}
          title="YouTube video player"
          frameborder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowfullscreen
        ></iframe>
      </div>
      <div className="mt-10">
        <h1 className="font-bold text-4xl dark:text-black">Movie Cast</h1>
      </div>

      <div className="grid grid-cols-2 gap-4">
        {actors &&
          actors.map((actor) => {
            return (
              <div className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-300 dark:border-gray-700 dark:hover:bg-gray-700">
                <div key={actor?.movieid} class="flex items-center space-x-4 ">
                  <img
                    class="w-36 h-36 rounded-full"
                    src={actor?.aimg_link}
                    alt=""
                  />

                  <div className="space-y-1 font-medium dark:text-white">
                    <div className="text-black">
                      {actor?.fname.concat(" ".concat(actor.lname))}
                    </div>
                    <div className="text-black">Gender: {actor.gender}</div>
                  </div>
                </div>
              </div>
            );
          })}
      </div>

      <div className="mt-10">
        <h1 className="font-bold text-4xl dark:text-black">Director</h1>
      </div>
      <div>
        {director &&
          director.map((director) => {
            return (
              <div className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-300 dark:border-gray-700 dark:hover:bg-gray-700">
                <div class="flex items-center space-x-4 ">
                  <img
                    class="w-36 h-36 rounded-full"
                    src={director?.dimg_link}
                    alt=""
                  />
                  <div class="space-y-1 font-medium dark:text-white ">
                    <div className="text-black hover:text-white ">
                      {director?.fname.concat(" ".concat(director.lname))}
                    </div>
                    <div className="text-black hover:text-white ">
                      Gender: {director?.gender}
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
      </div>
      <div>
        <form className="pt-10" onSubmit={(e) => {
                  addComment(e);
                }}>
          <div class="mb-4 w-full bg-gray-50 rounded-lg border border-gray-200 dark:bg-gray-700 dark:border-gray-600">
            <div class="py-2 px-4 bg-white rounded-t-lg dark:bg-gray-800">
              <label for="comment" class="sr-only">
                Your comment
              </label>
              <input
                type="text"
                value={firstname}
                class="px-0 w-full text-sm text-gray-900 bg-white border-0 dark:bg-gray-800 focus:ring-0 dark:text-white dark:placeholder-gray-400"
                placeholder="First Name"
                onChange={(e) => {
                  setFirstname(e.target.value);
                }}
                required=""
              ></input>
            </div>
            <div class="py-2 px-4 bg-white rounded-t-lg dark:bg-gray-800">
              <label for="comment" class="sr-only">
                Your comment
              </label>
              <textarea
                id="comment"
                rows="4"
                value={comments}
                onChange={(e) => {
                  setComments(e.target.value);
                }}
                class="px-0 w-full text-sm text-gray-900 bg-white border-0 dark:bg-gray-800 focus:ring-0 dark:text-white dark:placeholder-gray-400"
                placeholder="Write a comment..."
                required=""
              ></textarea>
            </div>
            <div class="flex justify-between items-center py-2 px-3 border-t dark:border-gray-600">
              <button
              type="submit"
                
                class="inline-flex items-center py-2.5 px-4 text-xs font-medium text-center text-white bg-blue-700 rounded-lg focus:ring-4 focus:ring-blue-200 dark:focus:ring-blue-900 hover:bg-blue-800"
              >
                Post comment
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
