import React, { useContext, useState, useEffect } from "react";
import MovieList from "../../api/MovieList";
import { MovieContext } from "../../context/MovieContext";
import { useParams } from "react-router-dom";

export default function VideoPlayer() {
  const { id } = useParams();
  // const [movieid, setMovieid] = useState(id);

  const { movies, setMovies } = useContext(MovieContext);

  const fetchData = async () => {
    try {
      // console.log("kfnsnfnksnfjnn")
      const video = await MovieList.get(`/get-movie-list/${id}`);
      // console.log(video);
      setMovies(video.data);
      console.log(video.data.data);
    } catch (error) {
      console.log(error.message);
      //redirect to homepage
    }
  };

  useEffect(() => {
    fetchData();
  }, [])
  

  return (
    <div className=" grid grid-rows-1 gap-4">
      <div>
        <iframe
          width="1000"
          height="500"
          src={movies.data?.movie_title.link}
          title="YouTube video player"
          frameborder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowfullscreen
        ></iframe>
      </div>
      <div className="mt-10">
        <h1>{movies.data?.movie_title.title}</h1>
      </div>
      <div className="grid grid-cols-2 gap-4">
        <div className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-300 dark:border-gray-700 dark:hover:bg-gray-700">
          <div class="flex items-center space-x-4 ">
            <img
              class="w-36 h-36 rounded-full"
              src="https://upload.wikimedia.org/wikipedia/commons/d/d5/Emilia_Clarke_by_Gage_Skidmore_2_%28cropped%29.jpg"
              alt=""
            />
            <div class="space-y-1 font-medium dark:text-white">
              <div className="text-black">Jese Leos</div>
              <div class="text-sm text-gray-500 dark:text-gray-400">
                Joined in August 2014
              </div>
            </div>
          </div>
        </div>
        <div class="flex items-center space-x-4">
          <img
            class="w-36 h-36 rounded-full"
            src="https://upload.wikimedia.org/wikipedia/commons/d/d5/Emilia_Clarke_by_Gage_Skidmore_2_%28cropped%29.jpg"
            alt=""
          />
          <div class="space-y-1 font-medium dark:text-white">
            <div className="text-black">Jese Leos</div>
            <div class="text-sm text-gray-500 dark:text-gray-400">
              Joined in August 2014
            </div>
          </div>
        </div>
      </div>
      <button onClick={fetchData}>button</button>
      <div className="mt-10">
        <h1>Director</h1>
      </div>
      <div>
        <div className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-300 dark:border-gray-700 dark:hover:bg-gray-700">
          <div class="flex items-center space-x-4 ">
            <img
              class="w-36 h-36 rounded-full"
              src="https://resizing.flixster.com/P_RaflXUozPVCVWqwWCPNaXuQCM=/218x280/v2/https://flxt.tmsimg.com/assets/753947_v9_ba.jpg"
              alt=""
            />
            <div class="space-y-1 font-medium dark:text-white ">
              <div className="text-black hover:text-white ">Jese Leos</div>
              <div class="text-sm text-gray-500 dark:text-gray-400">
                Joined in August 2014
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
